    color: #a4a4a4;
    margin-left: 40em;

`
const LoginBtn = styled(LoginButton)`
    margin-top: 2em;
    width: 13.5em;
    height: 4em;
`;

const Login = () => {
    return (
        <div style={{display:"flex", justifyContent: "center", alignItems: "center", height: "100vh", backgroundColor:"aliceblue"}}>
            <Container>
                <Logo src="/chackcheck.png"/>
                <InputBox placeholder="ID"></InputBox>
                <InputBox placeholder="PW"></InputBox>
                <AdminBtn>관리자로 로그인</AdminBtn>
                <LoginBtn/>
            </Container>
        </div>
    );
}

export default Login;
