import React, { useEffect, useState } from "react";
import { IoMdSearch } from "react-icons/io";
import styled from "styled-components"
import BookList from "./BookList";

const SearchBox = styled.input`
    width: 60em;
    height: 4.8em;
    border-radius: 3em;
    background-color: #fff;
    border: 0.15em solid transparent;
    background-image: linear-gradient(#fff, #fff),linear-gradient(to right,#FB8500,#4D4ABF);
    border-image-slice: 1;
    background-origin: border-box;
    background-clip: content-box, border-box;
`;

const gradientSVG = (
    <svg>
        <linearGradient id="gradient" x1="100%" y1="100%" x2="0%" y2="0%">
        <stop stopColor="#7a6ded" offset="0%" />
        <stop stopColor="#591885" offset="100%" />
        </linearGradient>
    </svg>
  );

const SearchButton = styled(IoMdSearch)`
    width: 3em;
    height: 3em;
    color: url(#gradient);
`;
const data = [
    "Apple",
    "Banana",
    "Orange",
    "Pineapple",
    "Strawberry",
    "Grapes",
    "Watermelon"
  ];

function SearchBar() {
    const [search, setSearch] = useState("");
    const [filteredData, setFilteredData] = useState([]);
    const onChange = (e) => setSearch(e.target.value);
    const onClick = () => {
        const newFilteredData = data.filter(item =>
            item.toLowerCase().includes(search.toLowerCase())
        );
        setFilteredData(newFilteredData);
    };



    return (
        <div>
            <SearchBox type="text" value={search} onChange={onChange}></SearchBox>
            <SearchButton type="button" onClick={onClick} />
            <div>
                {filteredData.map((item) => (
                    <BookList  
                        img={item.img}/>))}
            </div>
        </div>
    );
};


export default SearchBar;
