import React from "react";
import styled from "styled-components";

const Container = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    background-color: antiquewhite;
`
const Logo = styled.img`
    width: 13em;
    margin-bottom: 1em;
    margin-right: 13em;
`;

const InputBox = styled.input`
    display: block;
    border: none;
    border-bottom: solid 1px #A4A4A4;
    width: 35em;
    height: 3.5em;
    margin-bottom: 0.7em;
    -webkit-appearance: none;
    &:focus {
        outline: none;
        cursor: text;
    };
`;

const LoginButton = styled.div`
    font-family: 'NanumSquareOTF', sans-serif;
    font-weight: 900;
    color: transparent;
    width: 13em;
    height: 2.5em;
    text-align: center;
    -webkit-background-clip: text;
    line-height: 2.5em;
    border-radius: 0.5em;
    background-color: #fff;
    border: 0.15em solid transparent;
    background-image: linear-gradient(#fff, #fff),linear-gradient(to right,#4D4ABF,#FB8500);
    border-image-slice: 1;
    background-origin: border-box;
    background-clip: content-box, border-box, text;
`
const Login = () => {
    return (
        <div style={{display:"flex", justifyContent: "center", alignItems: "center", height: "100vh", backgroundColor:"aliceblue"}}>
            <Container>
                <Logo src="/chackcheck.png"/>
                <InputBox></InputBox>
                <InputBox></InputBox>
                <LoginButton>Login</LoginButton>
            </Container>
        </div>
    );
}

export default Login;
