import {React, useState} from "react";
import { IoMdSearch } from "react-icons/io";
import styled from "styled-components"
import BookList from "./BookList";
import { ReactComponent as SearchButton} from "../assets/searchButton.svg";

const Books = styled.div`
    display: grid;
    grid-template-columns: repeat(4, 1fr);
    grid-template-rows: reapeat(2, 1fr);
`;

const SearchBox = styled.input`
    width: 60em;
    height: 4.8em;
    border-radius: 3em;
    background-color: #fff;
    border: 0.15em solid transparent;
    background-image: linear-gradient(#fff, #fff),linear-gradient(to right,#FB8500,#4D4ABF);
    border-image-slice: 1;
    background-origin: border-box;
    background-clip: content-box, border-box;
`;

const SearchBtn = styled(SearchButton)`
    width: 3em;
    height: 3em;
`;

/*const gradientSVG = (
    <svg>
        <linearGradient id="gradient" x1="100%" y1="100%" x2="0%" y2="0%">
        <stop stopColor="#7a6ded" offset="0%" />
        <stop stopColor="#591885" offset="100%" />
        </linearGradient>
    </svg>
  );
*/
/*const SearchButton = styled(IoMdSearch)`
    width: 3em;
    height: 3em;
    color: url(#gradient);
`;*/
const data = [
    {
        index: 1,
        img: 'book1.jpg',
        title: 'The Great Gatsby',
        writer: 'F. Scott Fitzgerald',
        publisher: 'Scribner'
      },
      { 
        index: 2,
        img: 'book2.jpg',
        title: 'To Kill a Mockingbird',
        writer: 'Harper Lee',
        publisher: 'J.B. Lippincott & Co.'
      },
      {
        index: 3,
        img: 'book3.jpg',
        title: '1984',
        writer: 'George Orwell',
        publisher: 'Secker & Warburg'
      },


  ];

function SearchBar() {
    const [search, setSearch] = useState("");
    const [filteredData, setFilteredData] = useState([]);
    const onChange = (e) => setSearch(e.target.value);
    const onClick = () => {
        const newFilteredData = data.filter(item =>
            item.title.toLowerCase().includes(search.toLowerCase()) ||
            item.writer.toLowerCase().includes(search.toLowerCase()) ||
            item.publisher.toLowerCase().includes(search.toLowerCase())
        );
        setFilteredData(newFilteredData);
    };



    return (
        <div>
            <SearchBox type="text" value={search} onChange={onChange}>
                <SearchBtn type="button" onClick={onClick} /></SearchBox>

            <Books>
                {filteredData.map((item) => (<BookList
                    key={item.index}
                    img={item.img}
                    title={item.title}
                    writer={item.writer}
                    publisher={item.publisher}/>
                ))}
            </Books>
        </div>
    );
};


export default SearchBar;
