import styled from "styled-components"

const Books = styled.div`
    display: grid;
    grid-template-columns: repeat(4, 1fr);
    grid-template-rows: reapeat(2, 1fr);
`;

const Book = styled.div`
    display: flex;
    flex-direction: column;
`;
const Img = styled.img`
    display: inline-block;
    width: 11.563em;
    height: 13.563em;
    background-color: aliceblue;
    border: none;
    box-shadow: 0.4em 0.5em 0.5em 0em rgba(0,0,0,0.25), 0em 0.313em 0.875em 0em rgba(0,0,0,0.18);
`;
const Title = styled.span`
    font-family: 'NanumSquareOTF', sans-serif;
    font-size: 1.5em;
    font-weight: 800;
    margin-top: 0.8em;
    margin-bottom: 0.3em;
`;
const Writer = styled.span`
    font-family: 'NanumSquareOTF', sans-serif;
    font-size: 0.938;
    font-weight: 300;
`
const Publisher = styled.span`
    font-family: 'NanumSquareOTF', sans-serif;
    font-size: 0.938;
    font-weight: 300;
`;
const BookList = ({key, img, title, writer, publisher}) => {
    return(
        <Books>
        <Book key={key}>
            <Img src={img}></Img>
            <Title>{title}</Title>
            <div>
                <Writer>{writer}</Writer>
                <span style={{fontFamily: 'NanumSquareOTF, sans-serif', fontWeight: '300',fontSize: '0.938em', 
                        marginLeft: '0.5em', marginRight:'0.5em'}}>/</span>
                <Publisher>{publisher}</Publisher>
            </div>
        </Book>

        </Books>

    );
}

export default BookList;
