import React, { useEffect, useState } from "react";
import { IoMdSearch } from "react-icons/io";
import styled from "styled-components"

const SearchBox = styled.input`
    width: 60em;
    height: 4.8em;
    border-radius: 5em;
    background-color: #fff;
    border: 0.3 solid;
    border-image: linear-gradient(to right,#FB8500,#4D4ABF);

`;
const SearchButton = styled(IoMdSearch)`
    width: 3em;
    height: 3em;
    background-color: blueviolet;

`
const data = [
    "Apple",
    "Banana",
    "Orange",
    "Pineapple",
    "Strawberry",
    "Grapes",
    "Watermelon"
  ];

function SearchBar() {
    const [search, setSearch] = useState("");
    const [filteredData, setFilteredData] = useState([]);
    const onChange = (e) => setSearch(e.target.value);
    const onClick = () => {
        const newFilteredData = data.filter(item =>
            item.toLowerCase().includes(search.toLowerCase())
        );
        setFilteredData(newFilteredData);
    };



    return (
        <div>
            <SearchBox type="text" value={search} onChange={onChange}></SearchBox>
            <SearchButton type="button" onClick={onClick}></SearchButton>
            <div>
                {filteredData.map((item) => (<li>{item}</li>))}
            </div>
        </div>
    );
};


export default SearchBar;
